/* ==UserStyle==
@name           Atmosphere-O
@namespace      https://github.com/Catgrills/Atmosphere-O
@version        1.0.1
@description    An userstyle for 4chan called Pygmalion-Z.
@author         Catgrills/Katgrills
@preprocessor 	uso
@var select theme 'Theme' {
	"Light": `
	:root {
        --body-bg: #F3F3F3 !important;
		--card-bg: #E8EDF1 !important;
		--card-bg-2: #F8FAFB !important;
		--border-color: #BEBEBE !important;
		--link-color: #44B39D !important;
		--primary-color: #212121 !important;
		--secondary-color: #7E7E7E !important;
		--invert: invert(0) !important;
	}
	`,
	"Dark":`
	:root {
	    --body-bg: #242625 !important; 
		--card-bg: #2B2D2C !important;
		--card-bg-2: #101211 !important;
		--border-color: #101211 !important;
		--link-color: #44B39D !important;
		--primary-color: #FFF !important;
		--secondary-color: #EBEBEB !important;
		--invert: invert(1) !important;
	}
	`
}
==/UserStyle== */

@-moz-document domain("deviantart.com") {
/*[[theme]]*/
/* GENERAL */

	/** DESCRIPTION : set the main font and change others elements **/
	* {
		font-family: "Roboto", "ぼくたちのゴシック２ボールド";
	}

	*::-moz-focus-inner {
		border: 0 !important;
	}

	select {
		-webkit-appearance: none;
		-moz-appearance: none;
		text-indent: 0px;
		text-overflow: ellipsis;
		white-space: nowrap;
	}

	body,
    body.no-overhead,
	.alt0,
	.bubbleview,
	.chatroom .bubbleview,
    #container-overhead,
	#deviant,
	#deviant .gruze-main,
	.dev-page-container.bubbleview,
    .dev-page-view.view-mode-full .dev-view-deviation,
    .dev-page-view.view-mode-full_zoomed .dev-view-deviation,
	.discoverytag_container,
	#dv7,
    html,
    #ile-container, 
    #autocomplete-placebo, 
    #output #ile-contents .bubbleview,
	.messages,
	#output {
		background: var(--body-bg) !important;
	}

    body {
        overflow-x: hidden;
    }

    #output,
    .dev-page-container.bubbleview.minibrowse-container {
        margin-top: 80px !important;
    }

    .pagination .pages .prev .disabled,
    .pagination .pages .prev .away,
    .pagination .pages .next .disabled,
    .pagination .pages .next .away {
        /*** PAGINATION ***/
     	background: none !important;  
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        color: #FFF !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: bold !important;
        min-width: 100px !important;
        padding: 10px 5px !important;
        text-shadow: none !important;
        text-transform: uppercase !important;
    }

    .pagination .pages .prev .disabled,
    .pagination .pages .next .disabled {
        background: #3B3C3D !important;
    }

    .pagination .pages .prev .away {
        background: #008891 !important;
    }

    .pagination .pages .next .away {
        background: #00587A !important;        
    }

    .pagination .pages .number.current a,
    .pagination .pages .number a,
    .pagination ul.pages li.dotdotdot a {
        background: transparent;
        border: none !important;
        box-shadow: none !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        padding: 0px 10px !important;
        text-shadow: none !important;
        margin-top: 10px !important;
    }

    .pagination .pages .number.current a {
        color: var(--primary-color) !important;
        font-size: 14pt !important;
    }

    .browse-paging a.load_more,
    .hyperbuttons *,
    .smbutton.smbutton-big,
    .smbutton.smbutton-pale,
    .smbutton.smbutton-red,
    .smbutton.smbutton-wide,
    .moarbuttons .gmbutton2,
    .stash-upload #stash-form .smbutton,
    .show-more-button,
    .dev-page-view .dev-page-button,
    .ile-button.ile-handicapped {
        /*** BIG BUTTONS ***/
		background-image: none !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
		color: #FFF !important;
		font-family: "Roboto" !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: bold !important;
		letter-spacing: .2px !important;
		text-shadow: none !important;
		text-transform: uppercase !important;
    }

    .smbutton.smbutton-big *,
    .smbutton.smbutton-pale *,
    .smbutton.smbutton-wide *,
    .dev-page-view .dev-page-button *,
    .stash-upload #stash-form .smbutton *,
    .ile-button.ile-handicapped * {
        border: none !important;
		color: #FFF !important;
		font-family: "Roboto" !important;
		font-stretch: condensed !important;
		font-weight: bold !important;
		text-shadow: none !important;
		text-transform: uppercase !important;
    }

    .blues-bar .bb-with-sep,
    #deviant .gruze-sidebar,
    .gmbutton2,
    .gmbutton2 b,
    .gr-midbody {
     	background: transparent !important;   
    }

	.torpedo-container .thumb.faved .saved-faved-corner,
	.torpedo-container .thumb.saved .saved-faved-corner {
        /*** FAVORITE ICON ***/
		background: none !important;
	}

	.torpedo-container .thumb.faved .saved-faved-corner::after {
		color: #E7F76D;
	}

    .talk-post-reply .cx,
    #notes .compose_frame .gr-top .gr a.clear_note {
        /*** CLOSE ICON ***/
        background: url('https://imgur.com/4GlrMN7.png') no-repeat center !important;
		background-size: cover !important;
        filter: invert(1) !important;
    }

    .searchCanceller img {
        background: url('https://imgur.com/4GlrMN7.png') no-repeat center !important;
		background-size: cover !important;
        height: 0px !important;
        width: 0px !important;
        padding: 21px 0px 0px 21px !important;
        filter: var(--invert) !important;
    }

    #notes .compose_frame .gr-top .gr a.clear_note {
     	margin-top: 5px !important;   
    }

    .external {
        /*** LINKS ***/
     	color: var(--link-color) !important;   
    }

    .badge-stack,
    body.gruze .forum_uplift_topic a.commentfaq,
    .browse-container.newbrowse .browse-result-summary,
    .browse-divider.browse-page-divider,
    .browse-inline-ad,
    .browse-left-bar.browse-left-bar-inner .browse-link-groups span,
    .browse-left-bar.browse-left-bar-inner .browse-link-browse span,
    .browse-left-bar.browse-left-bar-inner .browse-link-prints span,
    .browse-facet-browse-links hr.browse-divider,
    .browse-page-header,
    #browse-results div.mlt-seed-container,
    #browse-results .mlt-seed-details,
    .browse-container.newbrowse .browse-top-bar .browse-explore-tags,
    .cc-seperator,
    .ch-ctrl.indifade,
    #comment .main-comment .c,
    #gmi-CCommentMaster .c,
    #gmi-GMFrame_Gruser .main-comment .c,
    .mods-column-ll #gmi-CCommentMaster .c,
    .commons_license_summary img,
    #comments .gr-top,
    #comments i.gr1, 
    #comments i.gr2, 
    #comments i.gr3,
    #comments-thread-settings,
    #deviantART-v7 #depths,
    #deviant .gmbutton2 .icon,
    #deviant .module-blocks .gr-top,
    #deviation_critiques i.l,
    .deviation-share-new.details-section,
	.deviation-full-minipage #gmi-CCommentMaster,
    .deviation-full-minipage .minipage-right #fave-btn-wrap,
    .deviation-full-minipage .minipage-right #fave-btn-wrap .favesnav-downarrow,
	.deviation-full-minipage .comments-count,
    .deviation-full-minipage .comments-count .comments-count-icon,
    .deviation-full-minipage .minipage-comments-disabled,
	.minipage-left-divider,
    .minipage-show-description.browse-proper-button,
    .dev-metainfo-copy-control,
	.dev-page-button.dev-page-download i,
    .dev-page-view .dev-meta-actions .dev-page-button.remove-message-button i,
    .dev-page-view .dev-view-meta hr.fade-highlight-bar,
    .dev-page-view #fave-btn-wrap #gmi-ResourceViewFavouriteButton em.iconx-favesstar,
    .dev-page-view .dev-page-button-drawer,
    .dev-page-view .dev-right-col-bg,
    .dev-page-view .dev-right-bar-title.group_featured_title,
    .dev-page-view .dev-title-container a.report-deviation-link.top-report-link,
    .dev-page-view .dev-view-meta .edit-buttons .delete-button i,
    .dev-page-view .dev-view-meta .edit-buttons .edit-button i,
    .dev-page-view .dev-meta-producttabs #print-button i,
    .dev-page-view .submit_to_groups_link i,
    .dev-page-view .dev-meta-producttabs #buy-tabs.dev-page-button,
    .dev-page-view .group_featured_list .user-symbol.group,
    .dev-right-bar-title.more-from-collection-title,
    .discoverytag_container .header .discoverytag_header_mature_filter,
    .discoverytag-hash,
    body.edit-history .edits,
    #fake-col-left,
    .feed-action .text-collapsed::before,
    .feed-action .text-collapsed::after,
    .feed-top-bar.feed_improvements,
    .forum_thread .forum_sidebar,
    .forum_uplift_topic .topic_username,
    #gmi-Gruser #deviant #gruze-columns .gr-box.gr-genericbox .gr-top h2 img,
    .gmbutton2town.moarbuttons .gmbutton2.gmbutton2qn2r .icon,
	.gr1,
	.gr2,
	.gr3,
	.gr1.gb,
	.gr2.gb,
	.gr3.gb,
	.gr-darkbox i.gt2 i,
	.gr-darkbox i.gt3 i,
	.gr-darkbox i.gt2,
	.gr-darkbox i.gt3,
	.dev-page-view .dev-page-below-tabs .gr1,
	.dev-page-view .dev-page-below-tabs .gr-darkbox i.dd1,
	.dev-page-view .dev-page-below-tabs .gr-darkbox i.dd2,
	.gr-darkbox .dd-icon,
	.gr-darkbox .dd-tile,
	.dev-page-view .dev-page-below-tabs .gr2.gb,
	.dev-page-view .dev-page-below-tabs .gr3.gb,
	.gr-headless i.gt2,
	.gr-headless i.gt3,
	.gr-headless i.gt3 i,
	.backroom .gr-headless i.gt2,
	.backroom .gr-headless i.gt3,
	.backroom .gr-headless i.gt3 i,
    .gr-recent_polls,
    .gr-top.gr-top-alone,
     .mods-column-ll .gr-top .gr i,
    .gruz-badgeholder.popup-unfriendly,
    .grbubbletip.renamed,
	i.tri,
    .journal-module .more-journals-link,
    .journal-module .journal-wrapper .journal-footer,
    #group .gruze-sidebar-end,
    #group .gr-bloggers,
    #group .more-journals-link,
    .match-body-height > .guideblock:last-child:not(.shineblock),
    .mature-tag.followed-by-report-button,
    .mcbox-inner.mcbox-inner-thumb.mcbox-inner-thumb-deviation .mcb-commentcount,
    .messages-menu #birthday-zone,
    .module-blocks #gmi-GMFrame_Gruser:first-child .gr-box .grf-indent .text,
    #notes .current-note span.sel-arrow,
    #notes .mcb-note-box .note-controls,
    #oh-menu-upgrade > a,
    .page-results #gmi-GPage #headerZone,
    .paging-results.paging-results-first-page.browse-link-button.popup-trigger,
    #pointsdownload_widget .dev-page-button i,
    .popup2-darkspeech,
    .search-box-clear.search-clear,
    .stash-create.stash-edit,
    .tab .icon,
    .user-symbol.beta,
    .user-symbol.premium,
    .user-symbol.senior,
    .watch-suggestions-home.visible {
     	display: none !important;   
    }

    /* HEADER */

    /** DESCRIPTION : change the elements of the header **/
    .deviantart #overhead-collect {
        background: #0F595E !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.2) !important;
        height: 70px !important;
        padding: 0px 15px 0px 10px !important;
    }

    .deviantart #overhead {
        /*** HEADER MAIN CONTAINER ***/
        background: #0F595E !important;
        border: none !important;
		height: 70px !important;
    }

    .deviantart #overhead #search7 #search7-ctrl input[name="q"] {
        /*** HEADER SEARCH BOX ***/
        background: transparent !important;
        border: none !important;
        border-radius: 1px !important;
        box-shadow: none !important;
        color: #FFF !important;
        font-family: "Roboto" !important;
        font-size: 15pt !important;
        font-stretch: condensed;
        font-weight: normal !important;
        height: 40px !important;
        letter-spacing: 0px !important;
    }

    .deviantart #overhead #search7 a {
		background: no-repeat url("https://i.imgur.com/VHRqhla.png") center !important;
		background-size: cover !important; 
        padding: 10px 0px 0px 10px !important;
        margin-top: 0px !important;
    }

    .deviantart #overhead #search7 a::after {
    	content: "" !important;
    }

    .deviantart #oh-menu-deviant,
    .deviantart #oh-menu-split, 
    .deviantart #oh-menu-friends, 
    .deviantart #oh-menu-collect, 
    .deviantart #oh-menu-cart, 
    .deviantart #oh-menu-join, 
    .deviantart #oh-loginbutton, 
    .deviantart #overhead .oh-mc-split, 
    .deviantart #oh-menu-greeting {
        /*** HEADER MENU ***/
        background: #0F595E !important;  
        border: none !important;
    }

    .deviantart #overhead #oh-menu-shop > a, 
    .deviantart #overhead #oh-menu-mobile > a, 
    .deviantart #overhead #oh-menu-more > a, 
    .deviantart #overhead #oh-menu-submit > a,
    .deviantart #oh-menu-deviant > a,
    .deviantart #oh-menu-split > a, 
    .deviantart #oh-menu-cart > a, 
    .deviantart #oh-menu-joinv, 
    .deviantart #oh-loginbutton > a, 
    .deviantart #overhead .oh-mc-split > a, 
    .deviantart #oh-menu-greeting > a,
    #oh-menu-join * {
    	color: #FFF !important;
        font-size: 12pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
	}

    .deviantart #oh-menu-friends > a, 
    .deviantart #oh-menu-collect > a {
    	color: #FFF !important;
        font-size: 20pt !important;
	}

    #overhead #collectlink.active,
    #overhead #friendslink.active {
        background: transparent !important;
        opacity: .6 !important;
    }

    .mmhover .oh-l {
        background: inherit !important;
    }

    .deviantart #overhead #oh-menu-submit > a > span {
        /*** HEADER MENU SUBMIT BUTTON ***/
     	border-radius: 2px !important;   
    }

    .deviantart #oh-menu-deviant .avatar {
        /*** HEADER MENU USER ***/
    	border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.5) !important;
        height: 25px !important;
        width: 25px !important;
        margin-top: 2px !important;
        margin-right: 10px !important;
    }

    .deviantart #oh-menu-deviant .username {
     	font-weight: bold !important; 
        text-transform: uppercase;
    }

	.deviantart .oh-l.oh-touch .icon {
		/*** HEADER MENU NOTIFICATIONS ICON ***/
		background: no-repeat url("https://i.imgur.com/lJAznYa.png") center !important;
		background-size: 18px auto !important;
		color: transparent !important;
	}

	.deviantart .oh-l[title="Notices"] .icon.i3 {
		/*** HEADER MENU NOTICE ICON ***/
		background: no-repeat url("https://i.imgur.com/xUijfgF.png") center !important;
		background-size: 24px auto !important;
		margin-top: -3px !important;
	}

	.deviantart #overhead-collect .oh-hashover.oh-keep.oh-mc-split.iconset-more i.icon.i1 {
		/*** HEADER MENU DEVIATION ICON ***/
		background: no-repeat url("https://i.imgur.com/ksIz1xz.png") center left !important;
		background-size: 16px auto !important;
		margin-top: -3px !important;
	}

	.deviantart #overhead-collect .oh-hashover.oh-keep.oh-mc-split.iconset-messages i.icon.i1 {
		/*** HEADER MENU WATCH ICON ***/
		background: no-repeat url("https://i.imgur.com/rBZ1wTs.png") center left !important;
		background-size: 16px auto !important;
		margin-top: -4px !important;
	}

	.deviantart #overhead-collect i.icon.i2 {
		/*** HEADER MENU FEEDBACK ICON ***/
		background: no-repeat url("https://i.imgur.com/pu4S5vj.png") center left !important;
		background-size: 16px auto !important;
		margin-top: -4px !important;
	}

	.deviantart #oh-menu-split.splintered > a {
		background: no-repeat url("https://i.imgur.com/OygFMxF.png") center !important;
		background-size: 16px auto !important;
		filter: brightness(0) invert(1);
	}

	#overhead .ntfirst,
	#overhead .ntmid,
	#overhead .ntlast {
		/*** HEADER PREV/ALL/NEXT BUTTONS ***/
		background: #586474 !important;
		border-color: #000 !important;
		border-radius: 1px !important;
		color: #FFF !important;
		font-family: "Roboto" !important;
		font-size: 12pt !important;
		font-stretch: condensed !important;
		font-weight: bold !important;
		padding-top: 3px !important;
		padding-bottom: 4px !important;
		padding-right: 16px !important;
		text-transform: uppercase !important;
		margin-top: 7px !important;
	}

	#overhead .ntfirst img,
	#overhead .ntmid img,
	#overhead .ntlast img {
		display: none !important;
	}

	.oh-menu {
        /*** HEADER GENERAL POPUP ***/
		background: #586474 !important;
	}

	.oh-menu-list-item i,
	#overhead .iconset-upsell .user-symbol.premium {
		display: none !important;
	}

	#overhead .mi {
		padding: 0px 10px !important;
	}

	.oh-menu .oh-darker .mi {
		margin-left: 30px !important;
	}

	.oh-menu .mi:hover {
		background: transparent !important;
        opacity: .6 !important;
	}

	.oh-hr,
	#overhead hr {
		border-top: 1px dotted #212121 !important;
		border-width: 1px 0px 0px 0px !important;
	}

    .oh-menu-list-item {
        padding-top: 3px !important;
    	padding-bottom: 3px !important;    
    }

	.oh-menu-list-item *,
    #overhead .oh-menu a {
        color: #FFF !important;
		font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: normal !important;
        line-height: 20pt !important;
	}

	.friendmachine .controls {
        /*** HEADER FRIENDS POPUP ***/
		display: none !important;
	}

	div.popup,
	div.popup2 {
		background: #586474 none repeat scroll 0% 0%;
		border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.3) !important;
	}

	.friendmachine .readout {
		background: #586474 none repeat scroll 0% 0%;
		border-color: transparent;
		color: #FFF !important;
		padding-left: 10px !important;
		position: absolute;
		top: 22px;
		right: 3px;
		bottom: 27px;
		left: 3px;
		overflow-y: auto;
		overflow-x: hidden;
		display: block !important;
	}

	.friendmachine .username {
		color: #FFF !important;
		font-family: "Roboto" !important;
		font-size: 14pt !important;
        font-stretch: condensed !important;
		font-weight: normal !important;
        line-height: 20pt !important;
	}

	.friendmachine .extra {
		color: #EBEBEB !important;
		font-family: "Roboto" !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-style: normal !important;
		font-weight: normal !important;
		text-transform: lowercase !important;
		padding-right: 15px !important;
	}

	#overhead-collect #lub {
		/*** HEADER FAVORITE POPUP ***/
		background: #0F595E !important;
		margin-top: -30px !important;
	}

	#lub .collections {
		background: #0F595E !important;
	}

    .collection-head *,
    #lub > .collection-head > .h1 {
        font-family: "Roboto" !important;
        font-size: 14pt !important;
        font-stretch: condensed !important;
    }

	#lub > div.collection-head {
        border-bottom: 1px dotted #212121 !important;
		margin-top: 18px !important;
	}

	.label {
		font-family: "Roboto" !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: bold !important;
		margin-top: -9px !important;
		text-transform: uppercase;
	}

    #lub div.tucked-away, 
    #lub div.tucked-away div.stackzone div.label, 
    #lub div.tucked-away div.stackzone div.label a {
        color: #EBEBEB !important;
    }

	.lubble {
		background: #0F595E !important;
		border-top: 1px dotted #212121;
	}

	.welcome {
		display: none !important;
	}

	.artzone {
		margin-left: 0px !important;
	}

	/* POPUP WINDOW */

	/** DESCRIPTION : change the lements in the popped up windows **/
	#modalspace {
		background: rgba(60, 55, 65, .6) !important;
	}

    .who-faved-modal .who-faved-iframe {
        border: none !important;
        box-shadow: none !important;
    }

	.modal.with-shadow,
	.shopmodal-container {

		/*** POPPED WINDOW ***/
		background: var(--card-bg) !important;
		border-color: rgba(190, 190, 190, .4) !important;
		border-radius: 2px !important;
		box-shadow: none !important;
	}

	.modal-devwatch .modal-devwatch-username {
		color: #1D628B !important;
		font-size: 12pt !important;
		font-weight: bold;
		padding-left: 15px;
	}

	.modal-devwatch h1,
	.modal-devwatch h2,
	.modal-devwatch h3,
	.fakedeepee-box .deepee-title strong,
	.first_option {

		/*** POPPED WINDOW TITLE ***/
		font-family: "Roboto" !important;
		font-size: 17pt !important;
        font-stretch: condensed !important;
		font-weight: normal !important;
	}

