	.fooview-inner p,
	.fooview-inner small {
		font-family: "Roboto" !important;
		font-size: 10pt;
	}

	#applications h2 {
		display: none !important;
	}

	.fooview h3 {
		background: var(--card-bg) !important;
        border-bottom: 1px dotted var(--border-color) !important;
        color: var(--primary-color) !important;
		font-family: "Roboto" !important;
        font-size: 18pt !important;
        font-stretch: condensed !important;
		font-weight: normal !important;
		letter-spacing: 0px !important;
		padding: 10px 15px 15px 15px !important;
		margin-left: -20px !important;
		margin-right: -20px !important;
		margin-top: -20px !important;
	}

	.fooview h4 {
		font-family: "Roboto" !important;
		font-weight: normal !important;
		letter-spacing: 0px !important;
	}

    .settings_identity .fooview-content *,
    .fooview-inner * {
    	color: var(--primary-color) !important;
        font-family: "Roboto" !important;
        font-size: 15pt !important;
        font-stretch: condensed !important;
    }

	.settings_identity #signature,
	.settings_index #blockedusers,
	.fooview input,
	.fooview select {
		background: var(--card-bg-2) !important;
		border: none !important;
		box-shadow: 0px 1px 2px rgba(0,0,0,.2) !important;
		font-family: "Roboto" !important;
		font-size: 15pt !important;
        font-stretch: condensed !important;
	}

	.l .smbutton,
	.fooview .smbutton.smbutton-pale {
		background: #8199A3 !important;
		border: none !important;
		border-radius: 2px !important;
		box-shadow: none !important;
		color: #FFF !important;
		font-family: "Roboto" !important;
		font-size: 14pt !important;
		font-stretch: condensed !important;
		font-weight: bold !important;
		padding: 5px !important;
		text-transform: uppercase;
	}

	.l .smbutton *,
	.fooview .smbutton.smbutton-pale * {
		border: none !important;
	}

	.smbutton.smbutton-green {
		background: #62C462;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0,0,0,.2) !important;
	}

	.smbutton.smbutton-green span {
        border: none !important;
        color: #FFF !important;
		font-size: 14pt !important;
        font-stretch: condensed !important;
        font-weight: bold !important;
        text-transform: uppercase !important;
	}

	.ch.misc_box {
		visibility: hidden;
	}

	.settings_deactivation .deactivation_choices .stay,
	#deactivate-now {
		border: none !important;
		border-radius: 2px !important;
		color: #FFF !important;
		font-family: "Roboto" !important;
		font-size: 11pt !important;
		font-stretch: condensed !important;
		font-weight: bold !important;
		padding: 5px !important;
		text-transform: uppercase !important;
	}

	.settings_deactivation .deactivation_choices .stay {
		background: #212121 !important;
	}

	.settings_sessions .settings-sessions-table tr.heading {
		display: none !important;
	}

	.settings_sessions .settings-sessions-table tr {
		background: #FFF !important;
	}

	.menu_holder {

		/*** SETTINGS SIDEBAR ***/
		background: transparent !important;
		border: none !important;
		padding: 20px !important;
        margin-top: -110px !important;
        margin-left: 0px !important;
		bottom: 0px !important;
	}

	ul.menu_holder li.header {
        color: var(--primary-color) !important;
		font-family: "Roboto" !important;
		font-size: 15pt !important;
		font-stretch: condensed !important;
		text-transform: uppercase !important;
        margin-top: 20px !important;
	}

	ul.menu_holder li > a {
		border-radius: 2px;
        color: var(--secondary-color) !important;
		font-family: "Roboto" !important;
		font-size: 14pt !important;
        font-stretch: condensed !important;
		padding-bottom: 12px !important;
	}

    ul.menu_holder li > a:not(.active):hover {
     	background: transparent;   
        color: var(--primary-color) !important;
    }

}

@-moz-document regexp("https://.*.deviantart.com/journal/poll/.*") {
    .blues-bar span.bb-with-sep, .blues-bar .bb-with-sep {
    	display: none !important;    
    }

